"""## Task 4: All-in-focus image and depth from focus (25 points)."""

#Task4: All-in-focus image and depth from the focus

from skimage.color import rgb2xyz
from scipy.ndimage import gaussian_filter

EXP = 1 #2.2
I_luminance = rgb2xyz(focal_stack.transpose(3, 0, 1, 2)**(EXP))[..., 1]

print('I_luminance range:', I_luminance.min(), I_luminance.max())
show_image(img=I_luminance[0], title='I_luminance', cmap='gray')

sigma1 =0.9 #2
I_low_freq = gaussian_filter(I_luminance, sigma=[0, sigma1, sigma1])
I_high_freq = I_luminance - I_low_freq

sigma2 = 4.5 #7#(3,3) is good
W_sharpness = gaussian_filter(I_high_freq**2, sigma=[0, sigma2, sigma2])


I_all_in_focus = (W_sharpness[:, :, :, None] * focal_stack.transpose(3, 0, 1, 2)).sum(0) / W_sharpness[:, :, :, None].sum(0)

Depth = (W_sharpness * depths[:, None, None]).sum(0) / W_sharpness.sum(0)

print('Depth range:', Depth.min(), Depth.max())

# sample intermediate outputs
show_image(img=I_low_freq[0], title='I_low-freq (s, t, d)', cmap='gray')
show_image(img=I_high_freq[0], title='I_high-freq (s, t, d)', cmap='gray')
show_image(img=W_sharpness[0], title='w_sharpness (s, t, d)', cmap='gray')
show_image(img=I_all_in_focus, title='I_all-focus (s, t, c)',)
show_image(img=Depth.max()-Depth, title='Depth (s, t)', cmap='gray')

